import { Component, OnInit, ViewChild } from '@angular/core';
import {
  ApexNonAxisChartSeries,
  ApexChart,
  ApexResponsive,
  ApexXAxis,
  ChartComponent,
} from 'ng-apexcharts';

export type AssetAllocationChartOptions = {
  series: ApexNonAxisChartSeries;
  chart: ApexChart;
  labels: any;
  responsive: ApexResponsive[];
};

export type MarketTrendChartOptions = {
  series: any;
  chart: ApexChart;
  xaxis: ApexXAxis;
};

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
})
export class DashboardComponent implements OnInit {
  @ViewChild('chart') chart!: ChartComponent;

  assetAllocationData!: Partial<any>;
  marketTrendData!: Partial<any>;

  ngOnInit(): void {
    this.assetAllocationData = {
      series: [44, 55, 13, 43],
      chart: {
        type: 'donut',
      },
      labels: ['Stocks', 'Bonds', 'ETFs', 'Cash'],
      responsive: [
        {
          breakpoint: 480,
          options: {
            chart: {
              width: 200,
            },
            legend: {
              position: 'bottom',
            },
          },
        },
      ],
    };

    this.marketTrendData = {
      series: [
        {
          name: 'Portfolio Value',
          data: [31000, 32000, 33000, 35000, 37000, 39000, 42000],
        },
      ],
      chart: {
        type: 'line',
        height: 350,
      },
      xaxis: {
        categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
      },
    };
  }
}
<div class="dashboard-container">
  <div class="metrics">
    <div class="card">
      <h3>Total Investment</h3>
      <p>$50,000</p>
    </div>
    <div class="card">
      <h3>Annual Return</h3>
      <p>12%</p>
    </div>
    <div class="card">
      <h3>Benchmark</h3>
      <p>10%</p>
    </div>
  </div>

  <div class="charts">
    <div class="chart-box">
      <h4>Asset Allocation</h4>
      <apx-chart
        [series]="assetAllocationData.series"
        [chart]="assetAllocationData.chart"
        [labels]="assetAllocationData.labels"
        [responsive]="assetAllocationData.responsive"
      ></apx-chart>
    </div>

    <div class="chart-box">
      <h4>Market Trends</h4>
      <apx-chart
        [series]="marketTrendData.series"
        [chart]="marketTrendData.chart"
        [xaxis]="marketTrendData.xaxis"
      ></apx-chart>
    </div>
  </div>
</div>
