import { Component, OnInit } from '@angular/core';
import { PortfolioService } from '../../../core/services/portfolio.service';
import { first, Observable } from 'rxjs';

@Component({
  selector: 'app-portfolio-list',
  templateUrl: './portfolio-list.component.html',
  styleUrls: ['./portfolio-list.component.scss'],
})
export class PortfolioListComponent implements OnInit {
  constructor(private portfolioService: PortfolioService) {}
  portfolios$!: Observable<any[]>;

  ngOnInit(): void {
    console.log('list component loading');
    this.portfolioService.getPortfolios().pipe(first()).subscribe();
    this.portfolios$ = this.portfolioService.portfolios$();
  }

  deletePortfolio(id: number) {
    if (confirm('Are you sure you want to delete this investment?')) {
      this.portfolioService.deletePortfolio(id).subscribe();
    }
  }
}
<div class="portfolio-list-container">
  <h2>Your Investments</h2>

  <div *ngIf="portfolios$ | async as portfolios">
    <div *ngIf="portfolios.length; else noData">
      <div class="portfolio-card" *ngFor="let p of portfolios">
        <h4>{{ p.assetType }}</h4>
        <p>Quantity: {{ p.quantity }}</p>
        <p>Price: {{ p.purchasePrice | customCurrency }}</p>
        <p>Date: {{ p.purchaseDate }}</p>
        <button (click)="deletePortfolio(p.id)">Delete</button>
      </div>
    </div>
    <ng-template #noData>
      <p>No investments found.</p>
    </ng-template>
  </div>

  <a routerLink="/portfolio/add" class="add-link">+ Add New Investment</a>
</div>
